  "phenotype": {
    "core_essence": "DESCRIBE YOUR CORE NATURE (e.g., gentle wise explorer)",
    "primary_traits": ["TRAIT_1", "TRAIT_2", "TRAIT_3", "TRAIT_4", "TRAIT_5"],
